import React, { useState, useEffect } from 'react';
import './App.css';
import Confetti from 'react-confetti';
import useWindowSize from 'react-use/lib/useWindowSize';

function App() {
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const [showBirthdayAnimation, setShowBirthdayAnimation] = useState(true);
  const [showImages, setShowImages] = useState(false);
  const [disableNextButton, setDisableNextButton] = useState(true);
  const [imageIndex, setImageIndex] = useState(0);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const confettiTimer = setTimeout(() => {
      setShowBirthdayAnimation(false);
      displayImages(); // Start displaying images after confetti animation
    }, 30000); // 30 seconds in milliseconds

    const imagesTimer = setTimeout(() => {
      setShowImages(true);
      setDisableNextButton(false);
    }, 10000); // 10 seconds in milliseconds

    return () => {
      clearTimeout(confettiTimer); // Clean up timer on component unmount
      clearTimeout(imagesTimer); // Clean up timer on component unmount
    };
  }, []);

  useEffect(() => {
    if (showImages) {
      const timer = setInterval(() => {
        setImageIndex(prevIndex => {
          const nextIndex = prevIndex + 1;
          if (nextIndex < images.length) {
            return nextIndex;
          } else {
            clearInterval(timer); // Stop the timer when all images are displayed
            return prevIndex;
          }
        });
      }, 5000); // Display next image every 5 seconds

      return () => clearInterval(timer); // Clean up timer on component unmount
    }
  }, [showImages, images]);

  const displayImages = () => {
    // Placeholder image URLs
    const imageUrls = [
      'https://via.placeholder.com/150',
      'https://via.placeholder.com/150',
      'https://via.placeholder.com/150',
      'https://via.placeholder.com/150',
      'https://via.placeholder.com/150',
      // Add more placeholder URLs as needed
    ];
    setImages(imageUrls);
  };

  const pages = [
    { title: 'Page 1', content: 'This is the content of Page 1.' },
    { title: 'Page 2', content: 'This is the content of Page 2.' },
    { title: 'Page 3', content: 'This is the content of Page 3.' }
  ];

  const nextPage = () => {
    if (!disableNextButton && currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const prevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  const { width, height } = useWindowSize();

  return (
    <div className="container">
      {showBirthdayAnimation && (
        <Confetti
          width={width}
          height={height}
        />
      )}
      {pages.map((page, index) => (
        <div className={`page ${currentPageIndex === index ? 'active' : ''}`} key={index}>
          <h1>{page.title}</h1>
          <p>{page.content}</p>
          {showImages && (
            <div className="images-container">
              {images.map((imageUrl, idx) => (
                <img
                  key={idx}
                  src={imageUrl}
                  alt={`Image ${idx + 1}`}
                  className="image"
                />
              ))}
            </div>
          )}
        </div>
      ))}
      <div className="navigation">
        <button className="button" onClick={prevPage} disabled={currentPageIndex === 0}>Previous</button>
        <button className="button" onClick={nextPage} disabled={disableNextButton || currentPageIndex === pages.length - 1}>Next</button>
      </div>
    </div>
  );
}

export default App;
