import React, { useState } from 'react';
import './App.css';

function App() {
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const pages = [
    { title: 'Page 1', content: 'This is the content of Page 1.' },
    { title: 'Page 2', content: 'This is the content of Page 2.' },
    { title: 'Page 3', content: 'This is the content of Page 3.' }
  ];

  const nextPage = () => {
    if (currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const prevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  return (
    <div className="container">
      {pages.map((page, index) => (
        <div className={`page ${currentPageIndex === index ? 'active' : ''}`} key={index}>
          <h1>{page.title}</h1>
          <p>{page.content}</p>
        </div>
      ))}
      <div className="navigation">
        <button className="button" onClick={prevPage} disabled={currentPageIndex === 0}>Previous</button>
        <button className="button" onClick={nextPage} disabled={currentPageIndex === pages.length - 1}>Next</button>
      </div>
    </div>
  );
}

export default App;
