import React, { useState, useEffect } from 'react';
import './App.css';
import Confetti from 'react-confetti';
import useWindowSize from 'react-use/lib/useWindowSize';

function App() {
  const [showBirthdayAnimation, setShowBirthdayAnimation] = useState(true);
  const [disableNextButton, setDisableNextButton] = useState(true); // Start with next button disabled
  const [messageIndex, setMessageIndex] = useState(0);
  const messages = [
    "Happy Birthday Natalia!",
    "Wishing you all the best on your special day!",
    "May your day be filled with joy and laughter!",
    "We love you!"
  ];

  useEffect(() => {
    const confettiTimer = setTimeout(() => {
      setShowBirthdayAnimation(false); // Turn off confetti after 15 seconds
      setDisableNextButton(false); // Enable next button after confetti animation
    }, 15000); // 15 seconds in milliseconds

    return () => clearTimeout(confettiTimer); // Clean up timer on component unmount
  }, []);

  useEffect(() => {
    if (showBirthdayAnimation) {
      const messageTimer = setInterval(() => {
        setMessageIndex(prevIndex => {
          const nextIndex = prevIndex + 1;
          if (nextIndex < messages.length) {
            return nextIndex;
          } else {
            clearInterval(messageTimer); // Stop the timer when all messages are displayed
            return prevIndex;
          }
        });
      }, 5000); // Display next message every 5 seconds during confetti animation

      return () => clearInterval(messageTimer); // Clean up timer on component unmount
    }
  }, [showBirthdayAnimation, messages]);

  const { width, height } = useWindowSize();

  return (
    <div className="container">
      {showBirthdayAnimation && (
        <Confetti
          width={width}
          height={height}
        />
      )}
      <div className="page">
        <h1>{messages[0]}</h1>
        {showBirthdayAnimation && messages.slice(1, messageIndex + 1).map((message, idx) => (
          <p key={idx}>{message}</p>
        ))}
        {messageIndex >= 3 && (
          <p>{messages[3]}</p>
        )}
      </div>
      <div className="navigation">
        <button className="button" disabled={disableNextButton}>Next</button>
      </div>
    </div>
  );
}

export default App;
