import React, { useState, useEffect } from 'react';
import './App.css';
import Confetti from 'react-confetti';
import useWindowSize from 'react-use/lib/useWindowSize';

function App() {
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const [showBirthdayAnimation, setShowBirthdayAnimation] = useState(true);
  const [showImage, setShowImage] = useState(false);

  useEffect(() => {
    const confettiTimer = setTimeout(() => {
      setShowBirthdayAnimation(false); // Hide confetti after 30 seconds
      clearInterval(imageTimer); // Clear image timer when confetti timer ends
    }, 30000); // 30 seconds in milliseconds

    const imageTimer = setInterval(() => {
      setShowImage(true); // Show image every 5 seconds
      setTimeout(() => setShowImage(false), 2000); // Hide image after 2 seconds
    }, 5000); // 5 seconds in milliseconds

    return () => {
      clearTimeout(confettiTimer); // Clean up confetti timer on component unmount
      clearInterval(imageTimer); // Clean up image timer on component unmount
    };
  }, []);

  const pages = [
    { title: 'Page 1', content: 'This is the content of Page 1.' },
    { title: 'Page 2', content: 'This is the content of Page 2.' },
    { title: 'Page 3', content: 'This is the content of Page 3.' }
  ];

  const nextPage = () => {
    if (currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const prevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  const { width, height } = useWindowSize();

  return (
    <div className="container">
      {showBirthdayAnimation && (
        <Confetti
          width={width}
          height={height}
        />
      )}
      {showImage && <img src="https://via.placeholder.com/150" alt="placeholder" />} {/* Display image if showImage is true */}
      {pages.map((page, index) => (
        <div className={`page ${currentPageIndex === index ? 'active' : ''}`} key={index}>
          <h1>{page.title}</h1>
          <p>{page.content}</p>
        </div>
      ))}
      <div className="navigation">
        <button className="button" onClick={prevPage} disabled={currentPageIndex === 0}>Previous</button>
        <button className="button" onClick={nextPage} disabled={currentPageIndex === pages.length - 1}>Next</button>
      </div>
    </div>
  );
}

export default App;
