import React, { useState, useEffect } from 'react';
import './App.css';
import Confetti from 'react-confetti';
import useWindowSize from 'react-use/lib/useWindowSize';

function App() {
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const [showBirthdayAnimation, setShowBirthdayAnimation] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowBirthdayAnimation(false); // Hide animation after 10 seconds
    }, 30000); // 10 seconds in milliseconds

    return () => clearTimeout(timer); // Clean up timer on component unmount
  }, []);

  const pages = [
    { title: 'Happy Birthday Natalia!', content: 'This is the content of Page 1.' },
    { title: 'Page 2', content: 'This is the content of Page 2.' },
    { title: 'Page 3', content: 'This is the content of Page 3.' }
  ];

  const nextPage = () => {
    if (currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const prevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  const { width, height } = useWindowSize();

  return (
    <div className="container">
      {showBirthdayAnimation && (
        <Confetti
          width={width}
          height={height}
        />
      )}
      {pages.map((page, index) => (
        <div className={`page ${currentPageIndex === index ? 'active' : ''}`} key={index}>
          <h1>{page.title}</h1>
          <p>{page.content}</p>
        </div>
      ))}
      <div className="navigation">
        <button className="button" onClick={prevPage} disabled={currentPageIndex === 0}>Previous</button>
        <button className="button" onClick={nextPage} disabled={currentPageIndex === pages.length - 1}>Next</button>
      </div>
    </div>
  );
}

export default App;
