import React, { useState, useEffect } from 'react';
import './App.css';
import Confetti from 'react-confetti';
import useWindowSize from 'react-use/lib/useWindowSize';

function App() {
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const [showBirthdayAnimation, setShowBirthdayAnimation] = useState(true);
  const [showImages, setShowImages] = useState(false);
  const [disableNextButton, setDisableNextButton] = useState(true); // Start with next button disabled
  const [imageIndex, setImageIndex] = useState(0);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const confettiTimer = setTimeout(() => {
      setShowBirthdayAnimation(false); // Turn off confetti after 30 seconds
      setShowImages(true); // Start displaying images
    }, 20000); // 30 seconds in milliseconds

    return () => clearTimeout(confettiTimer); // Clean up timer on component unmount
  }, []);

  useEffect(() => {
    if (showImages) {
      const imageTimer = setInterval(() => {
        setImageIndex(prevIndex => {
          const nextIndex = prevIndex + 1;
          if (nextIndex < images.length) {
            return nextIndex;
          } else {
            clearInterval(imageTimer); // Stop the timer when all images are displayed
            setDisableNextButton(false); // Enable next button after all images are displayed
            return prevIndex;
          }
        });
      }, 5000); // Display next image every 5 seconds

      return () => clearInterval(imageTimer); // Clean up timer on component unmount
    }
  }, [showImages, images]);

  useEffect(() => {
    if (showBirthdayAnimation) {
      const imageTimer = setInterval(() => {
        if (imageIndex < images.length) {
          setImageIndex(prevIndex => prevIndex + 1);
        }
      }, 5000); // Display next image every 5 seconds during confetti animation

      return () => clearInterval(imageTimer); // Clean up timer on component unmount
    }
  }, [showBirthdayAnimation, images, imageIndex]);

  const displayImages = () => {
    // Placeholder image URLs
    const imageUrls = [
      'https://via.placeholder.com/150/0000FF',
      'https://via.placeholder.com/150/00FF00',
      'https://via.placeholder.com/150/FF0000',
      'https://via.placeholder.com/150/FFFF00',
      'https://via.placeholder.com/150/FF00FF',
      'https://via.placeholder.com/150/00FFFF',
    ];
    setImages(imageUrls);
  };

  const pages = [
    { title: 'Happy Birthday Natalia!'},
    { title: 'Page 2', content: 'This is the content of Page 2.' },
    { title: 'Page 3', content: 'This is the content of Page 3.' }
  ];

  const nextPage = () => {
    if (!disableNextButton && currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const prevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  const { width, height } = useWindowSize();

  return (
    <div className="container">
      {showBirthdayAnimation && (
        <Confetti
          width={width}
          height={height}
        />
      )}
      {pages.map((page, index) => (
        <div className={`page ${currentPageIndex === index ? 'active' : ''}`} key={index}>
          <h1>{page.title}</h1>
          <p>{page.content}</p>
          {showImages && images.slice(0, imageIndex + 1).map((imageUrl, idx) => (
            <img
              key={idx}
              src={imageUrl}
              alt={`Image ${idx + 1}`}
              style={{ width: '100px', height: '100px', margin: '5px', objectFit: 'cover' }}
            />
          ))}
        </div>
      ))}
      <div className="navigation">
        <button className="button" onClick={prevPage} disabled={currentPageIndex === 0}>Previous</button>
        <button className="button" onClick={nextPage} disabled={disableNextButton || currentPageIndex === pages.length - 1}>Next</button>
      </div>
    </div>
  );
}

export default App;
